command:
let actor = game.user.character;
if (!actor) {
  // If we don't have our own character check what token is controlled on the canvas
  const selectedTokens = canvas.tokens.controlled;
  if (!selectedTokens || selectedTokens.length === 0) {
    ui.notifications.warn("No token selected!");
    return;
  }
  actor = selectedTokens[0].actor;
}
const die_type = [
  "d20",
  "d20",
  "d12",
  "d12",
  "d10",
  "d8",
  "d6",
  "d6",
  "d4"
]

if (actor.system.effective_level > 9) {
  ChatMessage.create({content: "[Fear] FEARLESS!", speaker: {actor: actor._id}});
} else {
  const pack = game.packs.get("neon-lords.tables");
  if (!pack) {
    ui.notifications.warn("Pack table not found!");
    return;
  }
  pack.getIndex();
  tableID = pack.index.find(t => t.name === 'Fear')._id;
  if (!tableID) {
    ui.notifications.warn("Fear table not found!");
    return;
  }
  const roll = new Roll(die_type[actor.system.effective_level - 1], {});
  pack.getDocument(tableID).then(table => table.draw({roll}));
}
