const actor = game.user.character;
if (!actor) {
  ui.notifications.warn("No character selected!");
  return;
}
const die_type = [
  "d20",
  "d20",
  "d12",
  "d12",
  "d10",
  "d8",
  "d6",
  "d6",
  "d4"
]

console.log(actor);

if (actor.system.level.value > 9) {
  ChatMessage.create({content: "[Fear] FEARLESS!", speaker: {actor: actor._id}});
} else {
  const pack = game.packs.get("neon-lords.tables");
  if (!pack) {
    ui.notifications.warn("Pack table not found!");
    return;
  }
  pack.getIndex();
  tableID = pack.index.find(t => t.name === 'Fear')._id;
  if (!tableID) {
    ui.notifications.warn("Fear table not found!");
    return;
  }
  const roll = new Roll(die_type[actor.system.level.value - 1], actor.getRollData());
  pack.getDocument(tableID).then(table => table.draw({roll}));
}
